import { useState, useEffect } from 'react';
import {
  BookOpen,
  Users,
  UserCircle,
  Building2,
  ClipboardList,
  Plus,
  Search,
  Edit2,
  Trash2,
  X,
  Check,
  Calendar,
  Mail,
  Phone,
  Euro,
  GraduationCap
} from 'lucide-react';
import { format } from 'date-fns';
import { de } from 'date-fns/locale';

// Types
interface Dozent {
  record_id?: string;
  name: string;
  email: string;
  telefon: string;
  fachgebiet: string;
}

interface Teilnehmer {
  record_id?: string;
  name: string;
  email: string;
  telefon: string;
  geburtsdatum: string;
}

interface Raum {
  record_id?: string;
  raumname: string;
  gebaeude: string;
  kapazitaet: number;
}

interface Kurs {
  record_id?: string;
  titel: string;
  beschreibung: string;
  startdatum: string;
  enddatum: string;
  max_teilnehmer: number;
  preis: number;
  dozent_id: string;
  raum_id: string;
}

interface Anmeldung {
  record_id?: string;
  teilnehmer_id: string;
  kurs_id: string;
  anmeldedatum: string;
  bezahlt: boolean;
}

type TabId = 'kurse' | 'dozenten' | 'teilnehmer' | 'raeume' | 'anmeldungen';

const tabs: { id: TabId; label: string; icon: React.ElementType }[] = [
  { id: 'kurse', label: 'Kurse', icon: BookOpen },
  { id: 'dozenten', label: 'Dozenten', icon: GraduationCap },
  { id: 'teilnehmer', label: 'Teilnehmer', icon: Users },
  { id: 'raeume', label: 'R√§ume', icon: Building2 },
  { id: 'anmeldungen', label: 'Anmeldungen', icon: ClipboardList },
];

// Mock data - will be replaced with API
const initialDozenten: Dozent[] = [
  { record_id: '1', name: 'Dr. Maria Schmidt', email: 'schmidt@kurse.de', telefon: '0171-1234567', fachgebiet: 'Informatik' },
  { record_id: '2', name: 'Prof. Hans M√ºller', email: 'mueller@kurse.de', telefon: '0172-2345678', fachgebiet: 'BWL' },
];

const initialTeilnehmer: Teilnehmer[] = [
  { record_id: '1', name: 'Anna Weber', email: 'anna.weber@mail.de', telefon: '0173-3456789', geburtsdatum: '1995-03-15' },
  { record_id: '2', name: 'Max Fischer', email: 'max.fischer@mail.de', telefon: '0174-4567890', geburtsdatum: '1988-07-22' },
];

const initialRaeume: Raum[] = [
  { record_id: '1', raumname: 'Seminarraum A', gebaeude: 'Hauptgeb√§ude', kapazitaet: 30 },
  { record_id: '2', raumname: 'EDV-Raum 1', gebaeude: 'Nebengeb√§ude', kapazitaet: 20 },
];

const initialKurse: Kurs[] = [
  { record_id: '1', titel: 'Python Grundkurs', beschreibung: 'Einf√ºhrung in Python', startdatum: '2024-03-01', enddatum: '2024-03-15', max_teilnehmer: 20, preis: 299, dozent_id: '1', raum_id: '2' },
  { record_id: '2', titel: 'BWL Basics', beschreibung: 'Grundlagen der Betriebswirtschaft', startdatum: '2024-04-01', enddatum: '2024-04-30', max_teilnehmer: 25, preis: 399, dozent_id: '2', raum_id: '1' },
];

const initialAnmeldungen: Anmeldung[] = [
  { record_id: '1', teilnehmer_id: '1', kurs_id: '1', anmeldedatum: '2024-02-15', bezahlt: true },
  { record_id: '2', teilnehmer_id: '2', kurs_id: '1', anmeldedatum: '2024-02-18', bezahlt: false },
];

export default function Dashboard() {
  const [activeTab, setActiveTab] = useState<TabId>('kurse');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);

  // Data states
  const [dozenten, setDozenten] = useState<Dozent[]>(initialDozenten);
  const [teilnehmer, setTeilnehmer] = useState<Teilnehmer[]>(initialTeilnehmer);
  const [raeume, setRaeume] = useState<Raum[]>(initialRaeume);
  const [kurse, setKurse] = useState<Kurs[]>(initialKurse);
  const [anmeldungen, setAnmeldungen] = useState<Anmeldung[]>(initialAnmeldungen);

  // Modal states
  const [showForm, setShowForm] = useState(false);
  const [editingItem, setEditingItem] = useState<any>(null);

  // Stats
  const stats = {
    kurse: kurse.length,
    dozenten: dozenten.length,
    teilnehmer: teilnehmer.length,
    raeume: raeume.length,
    anmeldungen: anmeldungen.length,
    bezahlt: anmeldungen.filter(a => a.bezahlt).length,
  };

  const handleAdd = () => {
    setEditingItem(null);
    setShowForm(true);
  };

  const handleEdit = (item: any) => {
    setEditingItem(item);
    setShowForm(true);
  };

  const handleDelete = (id: string) => {
    if (!confirm('Wirklich l√∂schen?')) return;

    switch (activeTab) {
      case 'dozenten':
        setDozenten(prev => prev.filter(d => d.record_id !== id));
        break;
      case 'teilnehmer':
        setTeilnehmer(prev => prev.filter(t => t.record_id !== id));
        break;
      case 'raeume':
        setRaeume(prev => prev.filter(r => r.record_id !== id));
        break;
      case 'kurse':
        setKurse(prev => prev.filter(k => k.record_id !== id));
        break;
      case 'anmeldungen':
        setAnmeldungen(prev => prev.filter(a => a.record_id !== id));
        break;
    }
  };

  const handleSave = (data: any) => {
    const id = editingItem?.record_id || Date.now().toString();
    const item = { ...data, record_id: id };

    switch (activeTab) {
      case 'dozenten':
        if (editingItem) {
          setDozenten(prev => prev.map(d => d.record_id === id ? item : d));
        } else {
          setDozenten(prev => [...prev, item]);
        }
        break;
      case 'teilnehmer':
        if (editingItem) {
          setTeilnehmer(prev => prev.map(t => t.record_id === id ? item : t));
        } else {
          setTeilnehmer(prev => [...prev, item]);
        }
        break;
      case 'raeume':
        if (editingItem) {
          setRaeume(prev => prev.map(r => r.record_id === id ? item : r));
        } else {
          setRaeume(prev => [...prev, item]);
        }
        break;
      case 'kurse':
        if (editingItem) {
          setKurse(prev => prev.map(k => k.record_id === id ? item : k));
        } else {
          setKurse(prev => [...prev, item]);
        }
        break;
      case 'anmeldungen':
        if (editingItem) {
          setAnmeldungen(prev => prev.map(a => a.record_id === id ? item : a));
        } else {
          setAnmeldungen(prev => [...prev, item]);
        }
        break;
    }
    setShowForm(false);
  };

  const getDozentName = (id: string) => dozenten.find(d => d.record_id === id)?.name || '-';
  const getTeilnehmerName = (id: string) => teilnehmer.find(t => t.record_id === id)?.name || '-';
  const getKursTitel = (id: string) => kurse.find(k => k.record_id === id)?.titel || '-';
  const getRaumName = (id: string) => raeume.find(r => r.record_id === id)?.raumname || '-';

  return (
    <div className="min-h-screen bg-background">
      {/* Sidebar */}
      <aside className="fixed left-0 top-0 h-full w-64 header-gradient text-sidebar-foreground flex flex-col z-10">
        <div className="p-6 border-b border-sidebar-border">
          <div className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-xl bg-sidebar-primary/20 flex items-center justify-center">
              <GraduationCap className="w-5 h-5 text-sidebar-primary" />
            </div>
            <div>
              <h1 className="font-bold text-lg">Kursverwaltung</h1>
              <p className="text-xs text-sidebar-foreground/60">Management System</p>
            </div>
          </div>
        </div>

        <nav className="flex-1 p-4 space-y-1">
          {tabs.map((tab) => (
            <button
              key={tab.id}
              onClick={() => setActiveTab(tab.id)}
              className={`nav-tab w-full ${activeTab === tab.id ? 'active' : ''}`}
            >
              <tab.icon className="w-5 h-5" />
              {tab.label}
            </button>
          ))}
        </nav>

        <div className="p-4 border-t border-sidebar-border">
          <div className="text-xs text-sidebar-foreground/50">
            {stats.anmeldungen} Anmeldungen total
          </div>
        </div>
      </aside>

      {/* Main Content */}
      <main className="ml-64 p-8">
        {/* Stats Header */}
        <div className="grid grid-cols-5 gap-4 mb-8">
          {tabs.map((tab) => (
            <div key={tab.id} className="stat-card">
              <div className="flex items-center justify-between mb-2">
                <span className={`badge-${tab.id}`}>{tab.label}</span>
                <tab.icon className="w-5 h-5 text-muted-foreground" />
              </div>
              <div className="stat-value">{stats[tab.id]}</div>
              <div className="stat-label">Eintr√§ge</div>
            </div>
          ))}
        </div>

        {/* Content Area */}
        <div className="card-elevated">
          {/* Header */}
          <div className="flex items-center justify-between p-6 border-b border-border">
            <h2 className="text-xl font-bold">
              {tabs.find(t => t.id === activeTab)?.label}
            </h2>
            <div className="flex items-center gap-4">
              <div className="relative">
                <Search className="w-4 h-4 absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground" />
                <input
                  type="text"
                  placeholder="Suchen..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="form-input pl-10 w-64"
                />
              </div>
              <button onClick={handleAdd} className="btn-accent">
                <Plus className="w-4 h-4" />
                Neu
              </button>
            </div>
          </div>

          {/* Table Content */}
          <div className="fade-in">
            {activeTab === 'dozenten' && (
              <DozentenTable
                data={dozenten.filter(d =>
                  d.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                  d.fachgebiet.toLowerCase().includes(searchTerm.toLowerCase())
                )}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            )}
            {activeTab === 'teilnehmer' && (
              <TeilnehmerTable
                data={teilnehmer.filter(t =>
                  t.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                  t.email.toLowerCase().includes(searchTerm.toLowerCase())
                )}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            )}
            {activeTab === 'raeume' && (
              <RaeumeTable
                data={raeume.filter(r =>
                  r.raumname.toLowerCase().includes(searchTerm.toLowerCase()) ||
                  r.gebaeude.toLowerCase().includes(searchTerm.toLowerCase())
                )}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            )}
            {activeTab === 'kurse' && (
              <KurseTable
                data={kurse.filter(k =>
                  k.titel.toLowerCase().includes(searchTerm.toLowerCase())
                )}
                getDozentName={getDozentName}
                getRaumName={getRaumName}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            )}
            {activeTab === 'anmeldungen' && (
              <AnmeldungenTable
                data={anmeldungen}
                getTeilnehmerName={getTeilnehmerName}
                getKursTitel={getKursTitel}
                onEdit={handleEdit}
                onDelete={handleDelete}
              />
            )}
          </div>
        </div>
      </main>

      {/* Form Modal */}
      {showForm && (
        <FormModal
          tab={activeTab}
          item={editingItem}
          onSave={handleSave}
          onClose={() => setShowForm(false)}
          dozenten={dozenten}
          teilnehmer={teilnehmer}
          kurse={kurse}
          raeume={raeume}
        />
      )}
    </div>
  );
}

// Table Components
function DozentenTable({ data, onEdit, onDelete }: {
  data: Dozent[];
  onEdit: (item: Dozent) => void;
  onDelete: (id: string) => void;
}) {
  return (
    <table className="data-table">
      <thead>
        <tr>
          <th>Name</th>
          <th>E-Mail</th>
          <th>Telefon</th>
          <th>Fachgebiet</th>
          <th className="w-24">Aktionen</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item.record_id}>
            <td className="font-medium">{item.name}</td>
            <td>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Mail className="w-4 h-4" />
                {item.email}
              </div>
            </td>
            <td>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Phone className="w-4 h-4" />
                {item.telefon}
              </div>
            </td>
            <td><span className="badge-dozenten">{item.fachgebiet}</span></td>
            <td>
              <div className="flex items-center gap-2">
                <button onClick={() => onEdit(item)} className="btn-ghost p-2">
                  <Edit2 className="w-4 h-4" />
                </button>
                <button onClick={() => onDelete(item.record_id!)} className="btn-ghost p-2 text-destructive">
                  <Trash2 className="w-4 h-4" />
                </button>
              </div>
            </td>
          </tr>
        ))}
        {data.length === 0 && (
          <tr>
            <td colSpan={5} className="text-center py-12 text-muted-foreground">
              Keine Dozenten gefunden
            </td>
          </tr>
        )}
      </tbody>
    </table>
  );
}

function TeilnehmerTable({ data, onEdit, onDelete }: {
  data: Teilnehmer[];
  onEdit: (item: Teilnehmer) => void;
  onDelete: (id: string) => void;
}) {
  return (
    <table className="data-table">
      <thead>
        <tr>
          <th>Name</th>
          <th>E-Mail</th>
          <th>Telefon</th>
          <th>Geburtsdatum</th>
          <th className="w-24">Aktionen</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item.record_id}>
            <td className="font-medium">{item.name}</td>
            <td>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Mail className="w-4 h-4" />
                {item.email}
              </div>
            </td>
            <td>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Phone className="w-4 h-4" />
                {item.telefon}
              </div>
            </td>
            <td>
              <div className="flex items-center gap-2 text-muted-foreground">
                <Calendar className="w-4 h-4" />
